const initialState = {
    counter: 0,
    name: "Dheeraj",
    store: []
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'INC_COUNTER':
            return {
                counter: state.counter + action.payload
            }


        case 'DEC_COUNTER':
            return {
                counter: state.counter - action.payload
            }
        default:
            return state;
    }
}
export default reducer;
